package sortpom.sort;import org.junit.jupiter.api.Test;import sortpom.util.SortPomImplUtil;class SortExecutionsTest {  @Test  void sortPhaseAndThenId() {    SortPomImplUtil.create()        .sortExecutions()        .lineSeparator("\r\n")        .testFiles("/SortExec_input_simpleWithPhase.xml", "/SortExec_expect_simpleWithPhase.xml");  }  @Test  void doNotSortIfPhaseAndIdIsSame() {    SortPomImplUtil.create()        .customSortOrderFile("sortOrderFiles/extra_dummy_tags.xml")        .sortExecutions()        .lineSeparator("\r\n")        .testFiles("/SortExec_input_complexWithPhase.xml", "/SortExec_expect_complexWithPhase.xml");  }}